function ibg(){let e=document.querySelectorAll(".ibg");for(var t=0;t<e.length;t++)e[t].querySelector("img")&&(e[t].style.backgroundImage="url("+e[t].querySelector("img").getAttribute("src")+")")}ibg();const burger=document.querySelector(".burger"),nav=document.querySelector(".nav"),body=document.querySelector("body"),logo=document.querySelector(".header__img").querySelector("img");console.log(logo);const moveBurger=()=>{burger.classList.contains("active")?(burger.classList.remove("active"),nav.classList.remove("active"),body.classList.remove("lock"),logo.classList.remove("active"),logo.setAttribute("src","./img/logo.svg")):(burger.classList.add("active"),nav.classList.add("active"),body.classList.add("lock"),logo.setAttribute("src","./img/light-logo.png"))};burger.addEventListener("click",moveBurger);const list=document.querySelectorAll(".footer-list");list.forEach(e=>{e.addEventListener("click",()=>{e.classList.contains("active")?e.classList.remove("active"):(list.forEach(e=>{e.classList.remove("active")}),e.classList.add("active"))})});